                jQuery.each(jQuery('body .modalQuizUnique').find(' #form_field4 .hair-goals-options .options .option-selected'), function(i, v){
                    ansFour.push(jQuery(this).attr('optVl'));
                })
                jQuery.each(jQuery('body .modalQuizUnique').find(' #form_field6 .last-sec .days .day-selected'), function(i, v){
                    ansSixDays.push(jQuery(this).attr('optVl'));
                })
                var ansSix= [
                    jQuery('body .modalQuizUnique').find(' #form_field6 .last-sec .options .option-selected label').text(),
                ]

                

                if(jQuery('body .modalQuizUnique').find(' #form_field6 .last-sec .options .option-selected').hasClass('vidCon')){
                    let a= 'error',
                        aNameA= 'error';
                    if(jQuery('body .modalQuizUnique').find(' #input-skype') != ''){
                        a= jQuery('body .modalQuizUnique').find(' #input-skype').val();
                        aNameA= "Skype ID";
                    }
                    else if(jQuery('body .modalQuizUnique').find(' #input-facetime') != ''){
                        a= jQuery('body .modalQuizUnique').find(' #input-facetime').val();
                        aNameA= "Facetime ID";
                    }
                    else if(jQuery('body .modalQuizUnique').find(' #input-zoomMeet') != ''){
                        a= jQuery('body .modalQuizUnique').find(' #input-zoomMeet').val();
                        aNameA= "Zoom Meeting ID";
                    }
                        
                    let b= [
                        jQuery('body .modalQuizUnique').find(' #iNumStart').val(),
                        jQuery('body .modalQuizUnique').find(' #iNumEnd').val(),
                        jQuery('body .modalQuizUnique').find(' #am-pm').val()
                    ];
                    ansSix.push(aNameA)
                    ansSix.push(a)
                    ansSix.push(b)
                    ansSix.push(ansSixDays)
                    // ansSix.push(jQuery('body .modalQuizUnique').find(' #myFile').val())
                    
                }
                else if(jQuery('body .modalQuizUnique').find(' #form_field6 .last-sec .options .option-selected').hasClass('contactMe')){
                    let b= [
                        jQuery('body .modalQuizUnique').find(' #iNumStart').val(),
                        jQuery('body .modalQuizUnique').find(' #iNumEnd').val(),
                        jQuery('body .modalQuizUnique').find(' #am-pm').val()
                    ];
                    ansSix.push(b)
                    ansSix.push(ansSixDays)
                    // ansSix.push(jQuery('body .modalQuizUnique').find(' #myFile').val())
                    
                }
                var allDt= [
                    {
                        que: jQuery('body .modalQuizUnique').find(' #form_field1 .heading h3').text(),
                        ans: jQuery('body .modalQuizUnique').find(' #form_field1 .gender-options').find('.option-selected').attr('genderVl')
                    },
                    {
                        que: jQuery('body .modalQuizUnique').find(' #form_field2 .heading h3').text(),
                        ans: jQuery('body .modalQuizUnique').find(' #form_field2 .age-options').find('.option-selected').attr('ageVl')
                    },
                    {
                        que: jQuery('body .modalQuizUnique').find(' #form_field3 .heading h3').text(),
                        ans: ansThr,
                    },
                    {
                        que: jQuery('body .modalQuizUnique').find(' #form_field4 .heading h3').text(),
                        ans: ansFour,
                    },
                    {
                        que: jQuery('body .modalQuizUnique').find(' #form_field5 .heading h3').text(),
                        ans: ansFive,
                    },
                    {
                        que: jQuery('body .modalQuizUnique').find(' #form_field6 .heading h3').text(),
                        ans: ansSix,
                    },
                ];
                
                let formData= new FormData();
                formData.append('file', jQuery('#myFile')[0].files[0]);
                formData.append('myData', JSON.stringify(allDt));
                
                jQuery.ajax({
                    url: 'https://www.precisionslim.com/wp-content/themes/precisionslim/hairMailData.php',
                    method: 'POST',
                    dataType: 'JSON',
                    // mimeType: "multipart/form-data",
                    data: formData,
                    contentType: false,
                    processData: false,
                    beforeSend: function() {
                  		jQuery('.submit-image-sec').show();
                  	},
                    success: function(data){
                        console.log('success');
                      jQuery('#form_field_end').css('display','block');
                      jQuery('.quiz-wrap').hide();
                      jQuery.each(jQuery('body .modalQuizUnique .option-selected'), function(i, v){
                      	jQuery(this).removeClass('option-selected');
                      })
                      jQuery.each(jQuery('body .modalQuizUnique .day-selected'), function(i, v){
                      	jQuery(this).removeClass('day-selected');
                      })
                      jQuery.each(jQuery('body .modalQuizUnique .completed'), function(i, v){
                      	jQuery(this).removeClass('completed');
                      })
                      jQuery('#form_field6').removeClass('show');
                      jQuery('#form_field1').addClass('show');
                      jQuery('.form_field1').addClass('active');
                      jQuery('#input-name,#input-email,#input-phone,#input-skype,#input-facetime,#input-zoomMeet,#iNumStart,#iNumEnd,#file').val('');
                      jQuery('#am-pm').val('am');
                      jQuery('#imageFile').text('');
                      jQuery('#imageFileLoader,.imageFileCancel,.req-msg').hide();
                    },
                    error: function(err){
                        console.log("err", err);
                    },
                   	complete: function() {
                  		jQuery('.submit-image-sec').hide();
                  	},
                })
                
                // console.log(allDt)
            })
        });
    </script>
